function Validation(values){
    let error = {}
    const email_pattern = /^[^\s@]+@[^s@]+\.[^\s@]+$/
    const senha_pattern = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9]{8,}$/

    if(values.email === "") {
        error.email = "Email n達o deve estar vazio"
    }
    else if(!email_pattern.test(values.email)) {
        error.email = "Email errado"
    }else {
        error.email = ""
    }

    if(values.senha === ""){
        error.senha = "Senha n達o deve estar vazia"
    }else if(!senha_pattern.test(values.senha)) {
        error.senha = "Senha errada"
    }else {
        error.senha = ""
    }
    return error;
}

export default Validation;
